// LogInScreen.js

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./LogInScreen.css";
import backgroundImage from "../images/bg.png";
import user from "../images/user.png";

const LogInScreen = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState(''); // State for email
  const [error, setError] = useState('');
  const [password, setPassword] = useState('');
  const enteredEmail = email;
  

  const handleLogIn = async () => {
    if (!email.endsWith('@tip.edu.ph')) {
      setError('Only email addresses with the "tip.edu.ph" domain are allowed.');
      return;
    }
    navigate("/home");
  };

  const handleTerms = (event) => {
    event.preventDefault();
    navigate("/terms");
  };

  return (
    <div className="login-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <img src={user} alt="Logo" />
      <div className="login-container">
        <h2>Log in</h2>
        {error && <div className="error-message">{error}</div>}
        <div className="input-container">
          <input
            type="text"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="input-container">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button className="login-click" onClick={handleLogIn}>
          Log In
        </button>
        <p>
          Don't have an account? <a href="/signup">Sign Up</a>
          <br />
          <button onClick={handleTerms}>Terms</button> 
          and 
          <button onClick={handleTerms}>Conditions</button>
        </p>
      </div>
    </div>
  );
};

export default LogInScreen;
