import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import { useLocation } from 'react-router-dom';

const MapComponent = () => {
  const location = useLocation();
  const mapCenter = location.state || { lat: defaultLat, lng: defaultLng }; // Replace defaultLat and defaultLng with default coordinates
const DEFAULT_LAT = 40.7128; // Example latitude (e.g., New York City)
const DEFAULT_LNG = -74.0060; // Example longitude (e.g., New York City)


  const mapRef = useRef(null);

  useEffect(() => {
    if (mapRef.current) {
      // This will run when the div is rendered
      const map = L.map(mapRef.current).setView([51.505, -0.09], 13);

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
        attribution: '© OpenStreetMap contributors'
      }).addTo(map);

      // If you want to add a marker at the map's center
      const marker = L.marker([51.505, -0.09]).addTo(map);
      marker.bindPopup('A pretty CSS3 popup.<br> Easily customizable.').openPopup();
    }
  }, []);

  return <div id="map" ref={mapRef} style={{ height: '400px', width: '100%' }} />;
};

export default MapComponent;
