import React, { useEffect } from 'react';
import L from 'leaflet';
import './MyMapComponent.css'; // Make sure to create this CSS file

const MyMapComponent = () => {
  useEffect(() => {
    let map;

    if (!window.myMap) {
      map = L.map('map-container').setView([51.505, -0.09], 13);
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
        attribution: '© OpenStreetMap contributors'
      }).addTo(map);
      window.myMap = map;
    } else {
      // If the map already exists, invalidate its size after ensuring it's displayed
      map = window.myMap;
      setTimeout(() => map.invalidateSize(), 100);
    }

    return () => {
      if (window.myMap) {
        window.myMap.remove();
        window.myMap = null;
      }
    };
  }, []); // Empty dependency array means this effect runs once on mount

  const goBack = () => {
    // You can use history from react-router or simply window.history.back();
    window.history.back();
  };
  return (
    <div id="map-container" style={{ height: '500px', width: '100%', position: 'relative' }}>
      <button onClick={goBack} className="back-button">
        Back
      </button>
    </div>
  );
};

export default MyMapComponent;
