// LogInScreen.js

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./LogInScreen.css";
import backgroundImage from "../images/bg.png";
import user from "../images/user.png";
import axios from 'axios'; // Import axios for API calls

const LogInScreen = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState(''); // State for email
  const [password, setPassword] = useState(''); // State for password
  const [error, setError] = useState('');

  const handleLogIn = async () => {
    if (!email.endsWith('@tip.edu.ph')) {
      setError('Only email addresses with the "tip.edu.ph" domain are allowed.');
      return; // Exit the function to prevent further execution
    }

    try {
      const response = await axios.post('http://localhost:4000/api/dormfindr/login', { email, password });
      if (response.status === 200) {
        navigate('/home');
      } else {
        setError('Login failed, please try again.');
      }
    } catch (error) {
      setError(error.response ? error.response.data.message : 'Login failed, please try again.');
    }
  };

  const handleTerms = () => {
    navigate('/terms');
  };

  return (
    <div className="login-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <img src={user} alt="Logo" />
      <div className="login-container">
        <h2>Log in</h2>
        {error && <div className="error-message">{error}</div>} {/* Display error if it exists */}
        <div className="input-container">
          <input type="text" placeholder="Email" />
        </div>
        <div className="input-container">
          <input type="password" placeholder="Password" />
        </div>
        <button className="login-click" onClick={handleLogIn}>
          Log In
        </button>
        <p>
          Don't have an account? <a href="/signup">Sign Up</a>
          <br></br>
          <b href="/terms" onClick={handleTerms}>Terms </b> 
          and 
          <b href="/terms" onClick={handleTerms}> Conditions</b>
        </p>
      </div>
    </div>
  );
};

export default LogInScreen;
