import React, { useEffect, useRef } from 'react';
import L from 'leaflet';
import { useLocation } from 'react-router-dom';

const DEFAULT_LAT = 40.7128; // Example latitude (e.g., New York City)
const DEFAULT_LNG = -74.0060; // Example longitude (e.g., New York City)

const MapComponent = () => {
  const location = useLocation();
  const mapRef = useRef(null);

  useEffect(() => {
    // Use location state if available, otherwise use default coordinates
    const mapCenter = location.state || { lat: DEFAULT_LAT, lng: DEFAULT_LNG };

    if (mapRef.current) {
      const map = L.map(mapRef.current).setView([mapCenter.lat, mapCenter.lng], 13);

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
        attribution: '© OpenStreetMap contributors'
      }).addTo(map);

      // Add a marker at the map's center
      const marker = L.marker([mapCenter.lat, mapCenter.lng]).addTo(map);
      marker.bindPopup('A pretty CSS3 popup.<br> Easily customizable.').openPopup();
    }
  }, [location]);

  return <div id="map" ref={mapRef} style={{ height: '400px', width: '100%' }} />;
};

export default MapComponent;
