import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios'; // Import axios for API calls
import "./SignUpScreen.css";
import backgroundImage from "../images/bg.png";
import user from "../images/user.png";

const SignUpScreen = () => {
  const navigate = useNavigate();
  const [selectedRole, setSelectedRole] = useState("TIP Community");
  const [username, setUsername] = useState(''); // State for username
  const [email, setEmail] = useState(''); // State for email
  const [password, setPassword] = useState(''); // State for password
  const [confirmPassword, setConfirmPassword] = useState(''); // State for confirm password

  const handleRoleChange = (event) => {
    setSelectedRole(event.target.value);
  };

  const handleSignUp = async () => {
    if (password !== confirmPassword) {
      alert("Passwords don't match.");
      return;
    }

    // Construct the user object
    const newUser = {
      username,
      email,
      password,
      role: selectedRole,
    };

    try {
      // Replace with your backend endpoint
      const response = await axios.post('http://localhost:4000', newUser);
      // If the signup is successful, navigate to the account verification page
      navigate("/login");
    } catch (error) {
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error('Signup error', error.response.data);
        // Handle the error response here
      } else if (error.request) {
        // The request was made but no response was received
        console.error('Signup error', error.request);
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Error', error.message);
      }
      // Handle general errors here, such as displaying a notification to the user
    }
  };

  const goToTerms = () => {
    navigate("/terms");
  };

  return (
    <div className="signup-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <img src={user} alt="Logo" />
      <div className="signup-container">
        <h2>Create an Account</h2>
        <div className="input2-container">
          <input type="text" placeholder="Enter your Username" onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div className="input2-container">
          <input type="text" placeholder="Enter your Email" onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div className="input2-container">
          <input type="password" placeholder="Enter your Password" onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div className="input2-container">
          <input type="password" placeholder="Confirm Password" onChange={(e) => setConfirmPassword(e.target.value)} />
        </div>
        <div className="role-dropdown-container">
          <select value={selectedRole} onChange={handleRoleChange}>
            <option value="TIP Community">TIP Community</option>
            <option value="Dormitory Provider">Dormitory Provider</option>
          </select>
        </div>
        <button className="signup-click" onClick={handleSignUp}>Sign Up as {selectedRole}</button>
        <p>
          Already have an account? <a href="/login">Log In</a>
          <br />
          <b onClick={goToTerms}>Terms </b> 
          and 
          <b onClick={goToTerms}> Conditions</b>
        </p>
      </div>
    </div>
  );
};

export default SignUpScreen;
