// LogInScreen.js

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./LogInScreen.css";
import backgroundImage from "../images/bg.png";
import user from "../images/user.png";

const LogInScreen = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState(''); // State for email
  const [error, setError] = useState('');
  const enteredEmail = email;
  const [password, setPassword] = useState('');

  const handleLogIn = async () => {
    try {
      const response = await fetch('/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        // If login is successful, navigate to the home screen
        navigate('/home');
      } else {
        // If login fails, display an error
        const errorData = await response.json();
        setError(errorData.message);
      }
    } catch (error) {
      setError('Failed to connect to the server.');
    }
  };

  const handleTerms = () => {
    navigate("/terms");
  };

  return (
    <div className="login-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <img src={user} alt="Logo" />
      <div className="login-container">
        <h2>Log in</h2>
        {error && <div className="error-message">{error}</div>} {/* Display error if it exists */}
        <div className="input-container">
          <input type="text" placeholder="Email" />
        </div>
        <div className="input-container">
          <input type="password" placeholder="Password" />
        </div>
        <button className="login-click" onClick={handleLogIn}>
          Log In
        </button>
        <p>
          Don't have an account? <a href="/signup">Sign Up</a>
          <br></br>
          <b href="/terms" onClick={handleTerms}>Terms </b> 
          and 
          <b href="/terms" onClick={handleTerms}> Conditions</b>
        </p>
      </div>
    </div>
  );
};

export default LogInScreen;
