import React, { useState } from "react";
import "./AccountVerificationScreen.css"
import VerifyBackground from "../images/accveriwobutton.png";
import { useNavigate } from "react-router-dom"; 

const AccountVerificationScreen = () => {
  const navigate = useNavigate(); 
  const [userEmail, setUserEmail] = useState(''); 
  const [otp, setOtp] = useState("");
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const navigateToLogin = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:4000/api/dormfindr/account-verification', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: userEmail }),
      });

      const data = await response.json();
      if (response.ok && data.success) {
        navigate("/login");
      } else {
        setError(data.error || 'Failed to verify account');
      }
    } catch (error) {
      setError("Error sending email: " + error.message);
    }
    setIsLoading(false);
  };

  return (
    <div className="Account-screen" style={{ backgroundImage: `url(${VerifyBackground})` }}>
      <div className="Account-container">
          <input
            type="text"
            placeholder="Enter OTP"
            value={otp}
            onChange={(e) => setOtp(e.target.value)}
          />
          <button className="verify-button" onClick={navigateToLogin}>
            Submit
          </button>
        </div>
      </div>

  );
};

export default AccountVerificationScreen;
