// LogInScreen.js

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./LogInScreen.css";
import backgroundImage from "../images/bg.png";
import user from "../images/user.png";
import axios from 'axios'; // Import axios for API calls

const LogInScreen = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState(''); // State for email
  const [error, setError] = useState('');
  const enteredEmail = email;
  const [password, setPassword] = useState('');
  
  const handleLogIn = async () => {
    if (!email.endsWith('@tip.edu.ph')) {
      setError('Only email addresses with the "tip.edu.ph" domain are allowed.');
      return; // Exit the function to prevent further execution
    }
    const newUser = {
      email,
      password,
    };
    try {
      // Replace with your backend endpoint
      const response = await axios.post('http://localhost:4000/api/dormfindr/login', newUser);
      navigate("/home");
    } catch (error) {
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error('Login error', error.response.data);
        // Handle the error response here
      } else if (error.request) {
        // The request was made but no response was received
        console.error('Login error', error.request);
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Error', error.message);
      }
      // Handle general errors here, such as displaying a notification to the user
    }
  };

  const handleTerms = () => {
    navigate("/terms");
  };

  return (
    <div className="login-screen" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <img src={user} alt="Logo" />
      <div className="login-container">
        <h2>Log in</h2>
        {error && <div className="error-message">{error}</div>} {/* Display error if it exists */}
        <div className="input-container">
          <input type="text" placeholder="Email" />
        </div>
        <div className="input-container">
          <input type="password" placeholder="Password" />
        </div>
        <button className="login-click" onClick={handleLogIn}>
          Log In
        </button>
        <p>
          Don't have an account? <a href="/signup">Sign Up</a>
          <br></br>
          <b href="/terms" onClick={handleTerms}>Terms </b> 
          and 
          <b href="/terms" onClick={handleTerms}> Conditions</b>
        </p>
      </div>
    </div>
  );
};

export default LogInScreen;
